import SalesDataService from './sales.js' ;

class SalesAnalysisDataService {

    static async calculateRatioItems(store) {

        var sales

        SalesDataService.find(store, "location")
                .then(response => {
                    sales = response.data.Sales ;
                })
                .catch(e => {
                    console.log(e);
                })

        var items = {}
        // {'pencil':3, 'eraser':10}
        var totalNumber = 0
        // 13

        sales.foreach(element => {
            element.items.forEach(item => {
                items[item.name]+=parseInt(item.quantity)
                totalNumber+=parseInt(item.quantity)
            })
        });

        var ratios = []

        var objectKeysArray = Object.keys(items)

        objectKeysArray.map((objKey) => {
            var objValue = items[objKey]
            // objVal holds quantity of each item
            var ratio = objValue/totalNumber
            var newJsonObject = {
                'category': objKey,
                'value': ratio
            }
            ratios.push(newJsonObject)
        })

        console.log(ratios)

        return ratios 
    }

}

