import React, { useEffect } from 'react';
import SalesDataService from '../../services/sales.js' ;
import dayjs from 'dayjs' ;
import { Chart } from "react-google-charts";
import {Link,Routes,Route} from 'react-router-dom';

const Graph2 = props => {
    return (
      <div className='wrapper App container'>
        <div className="input-group mb-3">
                      <div class="input-group-prepend">
                          <span class="input-group-text" id="basic-addon3">Year</span>
                      </div>

                      <input class="form-control" type="number" min="2013" max="2017" onChange={OnChangeSetYear} >
                      </input>

                      <div className="input-group-append">
                          <button
                          className="btn btn-outline-dark"
                          type="button"
                          onClick={DisplayPieForRevenueChart}
                          >
                          Analyze
                          </button>
                      </div>
                  </div>
        <div>
          {
            props.dataForAnnualPie?(<div>
              <div class="jumbotron jumbotron-fluid">
              <div class="container">
                <h1 class="display-6">2. Comparison of Annual Sales in stores</h1>
              </div></div>
              <Chart
              width={'800px'}
              height={'600px'}
              chartType="PieChart"
              loader={<div>Loading Chart</div>}
              data={dataForAnnualPie}
              options={{
                title: `% Revenue per outlet in the year ${queryYear}`,
                // chartArea: { left: 15, top: 15, right: 0, bottom: 0 },
                pieSliceText: "label",
              }}
              />
            </div>) :
            <div class="jumbotron jumbotron-fluid">
              <div class="container">
                <h1 class="display-6">2. Comparison of Annual Sales in stores</h1>
                <p class="lead text-italics">Select a year</p>
              </div>
            </div>
          }
        </div>
      </div>

    )
}

export default Graph2
