import React, { useState } from "react";
import '../App.css' ;

const Login = props => {

  const trueCredentials = {name: 'admin', id:'001'}

  const initialUserState = {
    name: "",
    id: "",
  };

  const [user, setUser] = useState(initialUserState);
  const [start, setStart] = useState(true);
  const [status, setStatus] = useState(true);
  const [name, setName] = useState();


  const handleNameInputChange = e => {
    setName(e.target.value) ;
  }
  const handlePasswordInputChange = event => {
    setPassword(e.target.value) ;
  };

  const login = () => {
    setUser({name, password}) ;
    setStart(false);
    if(user.name === trueCredentials.name && user.id === trueCredentials.id) {
      setStatus(true)
      props.login(user)
      props.history.push('/')
    }
    else {
      setStatus(false)
    }
  }

  return (

    <div className="App">
      {
        !start?
        (
          status?
          (            
            <div class="alert alert-success" role="success">
              Login successful
            </div>
          )
          : 
          (            
          <div class="alert alert-danger" role="alert">
            Invalid Crendentials - Please try again 
          </div>
          )
        )
        :
        null
      }

      <div className="col-lg-4 pb-1">
          <div className="card bg-dark text-white">
            <div className="card-body p-5 text-centre">
              <div class="mb-md-5 mt-md-4 pb-5">

                <h2 class="fw-bold mb-2 text-uppercase">Login</h2>
                <p class="text-white-50 mb-5">Please enter your login and password!</p>

                <div class="form-outline form-white mb-4">
                  <input type="text" class="form-control form-control-lg" onChange={handleNameInputChange} required/>
                  <label class="form-label" >Username</label>
                </div>

                <div class="form-outline form-white mb-4">
                  <input type="password" id="typePasswordX" class="form-control form-control-lg" value={user.id} onChange={handleInputChange} required/>
                  <label class="form-label" for="typePasswordX">Password</label>
                </div>

                <button class="btn btn-outline-light btn-lg px-5"  type="submit" onClick={login}>Login</button>

              </div>

            </div>
          </div>
        </div>

    </div>

  );
};

export default Login;


