import React from 'react';
import SalesDataService from '../services/sales.js'
import { useState } from 'react';
import { useEffect } from 'react';

const SalesList = props => {
  const [sales, setSales] = React.useState([])
  const [queryLocation, setQueryLocation] = React.useState("")
  const [queryCoupons, setQueryCoupons] = React.useState("")
  const [queryPurchaseMethod, setQueryPurchaseMethod] = React.useState("")
  const [purchaseMethods, setPurchaseMethods] = useState([]);

  useEffect(() => {
    retrieveSales();
    retrievePurchaseMethod();
  }, []);


  const onChangeQueryLocation = e => {
    const searchLocation = e.target.value;
    setQueryLocation(searchLocation);
  };

  const onChangeQueryCoupons = e => {
    var checkBox = document.getElementById("flexCheckChecked");
    var text = document.getElementById("text")
    const searchCoupons = (checkBox.checked?true:false);
    if (checkBox.checked == true){
      text.style.display = "block";
    } else {
      text.style.display = "none";
    }

    setQueryCoupons(searchCoupons);
  };

  const onChangeQueryPurchaseMethod = e => {
    const searchPurchaseMethod = e.target.value;
    setQueryPurchaseMethod(searchPurchaseMethod);
  };

  const retrieveSales = () => {
    console.log('here')
    SalesDataService.getAll()
      .then(response => {
        console.log(response.data);
        setSales(response.data.Sales);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const retrievePurchaseMethod = () => {
    SalesDataService.getPurchaseMethod()
      .then(response => {
        console.log(response.data);
        setPurchaseMethods(["Any"].concat(response.data));
      })
      .catch(e => {
        console.log(e);
      });
  };

  const refreshList = () => {
    retrieveSales();
  };

  const find = (query, by) => {
    SalesDataService.find(query, by)
      .then(response => {
        console.log(response.data);
        setSales(response.data.Sales);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const findByLocation = () => {
    find(queryLocation, "location")
  };

  const findByCoupons = () => {
    find(queryCoupons, "couponUsed")
  };

  const findByPurchaseMethod = () => {
    if (queryPurchaseMethod === "Any") {
      refreshList();
    } else {
      find(queryPurchaseMethod, "purchaseMethod")
    }
  };


  return (
    <div>
      <div className="row pb-1">

        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by location"
            value={queryLocation}
            onChange={onChangeQueryLocation}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByLocation}
            >
              Search
            </button>
          </div>
        </div>
        <div class="input-group col-lg-4">
          <input class="form-check-input" type="checkbox" value="" id="flexCheckChecked" onClick={onChangeQueryCoupons}/>
          <label class="form-check-label" for="flexCheckChecked">
            Coupons
          </label>
          <p id="text" style="display:none">Checkbox is CHECKED!</p>
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByCoupons}
            >
              Search
            </button>
          </div>
        </div>
        {/* <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by coupons"
            value={queryCoupons}
            onChange={onChangeQueryCoupons}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByCoupons}
            >
              Search
            </button>
          </div>
        </div> */}

        <div className="input-group col-lg-4">
          <select onChange={onChangeQueryPurchaseMethod}>
            {
              purchaseMethods.map(method => {
                return (
                  <option value={method}> {method} </option>
                )
              })
            }
          </select>
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByPurchaseMethod}
            >
              Search
            </button>
          </div>

        </div>

      </div>

      <div className="row">
        {

        sales.map((sale) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{sale.storeLocation}</h5>
                  <p className="card-text">
                    <strong>Customer: </strong>{sale.customer.email}<br/>
                    <strong>Ratings: </strong>{sale.customer.satisfaction}<br/>
                    <strong>Purchase Method: </strong>{sale.purchaseMethod}<br/>
                    <strong>Coupon Used:</strong>{
                      sale.couponUsed?"YES":"NO"
                    }<br/>
                  </p>
                  {/* <div className="row">
                  <Link to={"/sales/"+sale._id} className="btn btn-primary col-lg-5 mx-1 mb-1">
                    View Reviews
                  </Link>
                  <a target="_blank" href={"https://www.google.com/maps/place/" + address} className="btn btn-primary col-lg-5 mx-1 mb-1">View Map</a>
                  </div> */}
                </div>
              </div>
            </div>
          );
        })

        }


      </div>

    </div>
  );
}

export default SalesList;
