import React, { useEffect , useState } from 'react';
import SalesDataService from '../../services/sales.js' ;
import dayjs from 'dayjs' ;
import { Chart } from "react-google-charts";
import {Link,Routes,Route} from 'react-router-dom';

const Graph1 = props => {

  const [sales, setSales] = React.useState([])
  const [dataForPiePlot, setdataForPiePlot] = useState(null)
  const [store, setStore] = useState('')

    useEffect(() => {
      retrieveLocations();
    }, []);

    const calculateQuantityItems = () => {

        var items = {}

        sales.map(element => {

            element.items.map(item => {

              items[item.name]=(items[item.name] ? items[item.name]+parseInt(item.quantity) : parseInt(item.quantity))

            })
        });
        var ratios = [['Category','Quantity sold']]
        var objectKeysArray = Object.keys(items)
        objectKeysArray.forEach((objKey) => {
            var objValue = items[objKey]
            // objVal holds quantity of each item
            var ration = [objKey, objValue]
            ratios.push(ration)
        })

        return ratios
    }

    const DisplayPieChart = () => {
        SalesDataService.find(store, "location")
                .then(response => {       
                    setSales(response.data.Sales)
                })
                .catch(e => {
                    console.log(e);
                })

        const series = calculateQuantityItems()

        setdataForPiePlot(series)
    }

    const OnChangeSetStore = e => {
        setStore(e.target.value)
    }

    return (
        <div className='wrapper App container'>
        <div className="form-outline form-white">
        <select class="form-control selectinput" type='select'  onChange={OnChangeSetStore}>
            {
              locations.map(method => {
                return (
                  <option value={method}> {method} </option>
                )
              })
            }
          </select>
          <label class="form-label" for="formWhite2">Location</label>
          <div className="input-group-append">
              <button className="btn btn-outline-dark" onClick={DisplayPieChart}>Analyse</button>
          </div>
      </div>
      <div>
      {
          dataForPiePlot?(
        <div>
            <div class="jumbotron jumbotron-fluid">
            <div class="container">
              <h1 class="display-6">1. Sales % Of Items in A store</h1>
            </div>
          </div>
          <Chart
              width={'800px'}
              height={'600px'}
              chartType="PieChart"
              loader={<div>Loading Chart</div>}
              data={dataForPiePlot}
              options={{
                title: `Percentage of items Sold in ${store}`,
                pieSliceText: "label",
              }}
            />
          </div>) :
          <div class="jumbotron jumbotron-fluid">
            <div class="container">
              <h1 class="display-6">1. Sales % Of Items in A store</h1>
              <p class="lead text-italics">Select a store</p>
            </div>
          </div>
      }
      </div>
      </div>
    )
}

export default Graph1
