import React, { useEffect } from 'react';
import SalesDataService from '../services/sales.js' ;
import SalesAnalysisDataService from '../services/analyze.js' ;
import {
    Chart,
    ChartSeries,
    ChartSeriesItem,
    ChartCategoryAxis,
    ChartCategoryAxisItem,
    ChartTitle,
    ChartLegend
} from '@progress/kendo-react-charts';
import "hammerjs";

const AnalyzeSales = (props) => {
    // G1 : revenue from each all stores -> min & max = one graph has all stores

    // G2 : percentage of different items sold from a given store

    const [store, setStore] = React.useState("")
    const [locations, setLocations] = React.useState([])
    const [dataToPlot, setdataToPlot] = React.useState()

  useEffect(() => {
    retrieveLocations();
  }, []);

    const retrieveLocations = () => {
        SalesDataService.getStoreLocations()
        .then(response => {
        // console.log(response.data);
        setLocations(response.data);
        })
        .catch(e => {
        console.log(e);
        });
    }

    const OnChangeSetStore = e => {
        setStore(e.target.value)
    }

    const labelContent = (props) => {
        let formatedNumber = Number(props.dataItem.value).toLocaleString(undefined, {
          style: "percent",
          minimumFractionDigits: 2,
        });
        return `${props.dataItem.category} item: ${formatedNumber}`;
      };

      const ChartContainer = () => (
        <Chart>
          <ChartTitle text="Ratios of Items Sold" />
          <ChartLegend position="bottom" />
          <ChartSeries>
            <ChartSeriesItem
              type="pie"
              data={dataToPlot}
              field="value"
              categoryField="category"
              labels={{ visible: true, content: labelContent }}
            />
          </ChartSeries>
        </Chart>
      );

      const DisplayPieChart = () => {

        const response = SalesAnalysisDataService.calculateRatioItems(store)

        setdataToPlot(response)         

    }

      return (
        <div>
            <div className="row form">            
                <div className="input-group mb-3">
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="basic-addon3">@</span>
                    </div>

                    <select class="form-control" onChange={OnChangeSetStore}>
                        {
                        locations.map(method => {
                            return (
                            <option value={method}> {method} </option>
                            )
                        })
                        }
                    </select>

                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={DisplayPieChart}
                        >
                        Analyze
                        </button>
                    </div>
                </div>
            </div>


            <div className="row">

            {
                  // dataToPlot.map((sale) => {
                  //   return (
                  //     <div className="col-lg-4 pb-1">
                  //       <div className="card">
                  //         <div className="card-body">
                  //           <h5 className="card-title">{sale.storeLocation}</h5>
                  //           <p className="card-text">
                  //             <strong>Customer :  </strong>{sale.customer.email}<br/>
                  //             <strong>Ratings : </strong>{sale.customer.satisfaction}<br/>
                  //             <strong>Purchase Method : </strong>{sale.purchaseMethod}<br/>
                  //             <strong>Coupon Used :</strong>{
                  //               sale.couponUsed?" YES":" NO"
                  //             }<br/>
                  //           </p>
                  //         </div>
                  //       </div>
                  //     </div>
                  //   );
                  // })
                  dataToPlot.storeLocation
            }
            </div>

            {/* <Chart>
                <ChartTitle text="Ratios of Items Sold" />
                <ChartLegend position="bottom" />
                    <ChartSeries>
                        <ChartSeriesItem
                        type="pie"
                        data={dataToPlot}
                        field="value"
                        categoryField="category"
                        labels={{ visible: true, content: labelContent }}
                        />
                    </ChartSeries>
                </Chart>
                 */}

         </div> 

      );

}

export default AnalyzeSales;
