import './App.css';
import React from 'react';
import { Route, Routes, Link  } from 'react-router-dom' ;
import "bootstrap/dist/css/bootstrap.min.css" ;
import  SalesList  from './components/sales-list.js'
import  AddSale  from './components/add-sale.js'
import  DeleteSale  from './components/delete-sale.js'
import  Login  from './components/login.js'
import  Sale  from './components/sale.js'

function App() {

  const [user, setUser] = React.useState(null)

  async function login(user) {
    setUser(user)
  }

  async function logout() {
      setUser(null)
  }

  return (
      <div className="app">
        <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
          <a class="navbar-brand" href="#">EinDuch Shop</a>
          <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">

              <li class="nav-item">
                <Link to={"/sales"} className="nav-link" >All Sales</Link>
              </li>

              <li class="nav-item">
                <Link to={"/sale"} className="nav-link">Sale</Link>
              </li>

              <li class="nav-item">
                <Link to={"/delete-sale"} class="nav-link">Delete Sale</Link>
              </li>
              <li className="nav-item" >
              { 
              user ? (
                <a onClick={logout} className="nav-link" style={{cursor:'pointer'}}>
                  Logout {user.name}
                </a>
              ) : (            
                <a href="/login" className="nav-link">
                  Login
                </a>
                )}
            </li>
              <li class="nav-item">
                <a class="nav-link disabled" href="#">Disabled</a>
              </li>

            </ul>
          </div>
        </nav>
       Welcome to Eindach !
       <div className="container mt-3">
        <Routes>
          <Route exact path={["/","/supply", "supply/sales"]} component={SalesList} />
          <Route 
            path="/supply/sale"
            render={(props) => (
              <AddSale {...props} user={user} />
            )}
          />
          <Route 
            path="/supply/sale/:id"
            render={(props) => (
              <Sale {...props} user={user} />
            )}
          />
          <Route 
            path="/login"
            render={(props) => (
              <Login {...props} login={login} />
            )}
          />
        </Routes>
      </div>
    </div>
  );
}

export default App;
