import React, { useState } from 'react';
import SalesDataService from '../services/sales.js'
import { useEffect } from 'react';
import '../App.css'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Chart } from "react-google-charts";
import dayjs from 'dayjs' ;


const SalesList = props => {
  const [sales, setSales] = React.useState([])
  const [queryLocation, setQueryLocation] = React.useState("")
  const [queryCoupons, setQueryCoupons] = React.useState()
  const [queryPurchaseMethod, setQueryPurchaseMethod] = React.useState("")
  const [purchaseMethods, setPurchaseMethods] = React.useState(["Any"]);
  const [pages, setQueryPage] = React.useState(0) 
  const [locations,  setLocations] = React.useState(["All"])
  const [startDate, setstartDate] = React.useState(new Date())
  const [endDate, setendDate] = React.useState(new Date())
  const [NoSalesFlag, setNoSalesFlag] = React.useState(false)
  const [modifiedSales, setModifiedSales] = React.useState([])
  const [totalSales, settotalSales] = useState(0)

  useEffect(() => {
    retrieveSales();
    retrievePurchaseMethod();
    retrieveLocations();
    setQueryCoupons(false);
  }, []);

  const onChangeQueryLocation = e => {
    const searchLocation = e.target.value;
    setQueryLocation(searchLocation);
  };

  const onChangeQueryCoupons = e => {
    const searchCoupons = (e.target.checked?true:false)
    setQueryCoupons(searchCoupons);
  };

  const onChangeQueryPurchaseMethod = e => {
    const searchPurchaseMethod = e.target.value;
    setQueryPurchaseMethod(searchPurchaseMethod);
  };

  const onChangeSetstartDate = e => {
    setstartDate(e)
  }

  const onChangeSetendDate = e => {
    setendDate(e)
  }

  const retrieveSales = () => {
    SalesDataService.getAll()
      .then(response => {
        setSales(response.data.Sales);
      })
      .catch(e => {
        console.log(e);
      });
      setNoSalesFlag(true)
  };

  const retrievePurchaseMethod = () => {
    SalesDataService.getPurchaseMethod()
      .then(response => {
        setPurchaseMethods(["Any"].concat(response.data));
        setNoSalesFlag(true)
      })
      .catch(e => {
        console.log(e);
      });

  };

  const retrieveLocations = () => {
    SalesDataService.getStoreLocations()
    .then(response => {
      setLocations(["All"].concat(response.data));
      setNoSalesFlag(true)
    })
    .catch(e => {
      console.log(e);
    });
  }

  const refreshList = () => {
    retrieveSales();
    setNoSalesFlag(true)
  };

  const find = (query, by) => {
    SalesDataService.find(query, by)
      .then(response => {

        setSales(response.data.Sales);

        modifySalesFormat() 
        setNoSalesFlag(true)
      })
      .catch(e => {
        console.log(e);
      });
  };

  const modifySalesFormat = () => {

    var modified = [['Location','Date (MM-DD-YYYY)','Customer','Rating','Purchase Method','Coupon used']]
    sales.map(sale => {
      if(dayjs(sale.saleDate) > dayjs(startDate) && dayjs(sale.saleDate) < dayjs(endDate)) {
        var m = []
        m.push(sale.storeLocation)
        m.push(dayjs(sale.saleDate).format('MM-DD-YYYY'))
        m.push(sale.customer.email)
        m.push(sale.customer.satisfaction)
        m.push(sale.purchaseMethod)
        m.push(sale.couponUsed)
        modified.push(m)  
      }
    })

    modified.sort((a,b) => (dayjs(b[1]).toDate())-(dayjs(a[1]).toDate()))

    setModifiedSales(modified)

  }

  const findAll = () => {
    SalesDataService.getAll(pages)
      .then(response => {
          setSales(response.data.Sales)
      })
  }

  const findByLocation = () => {
    if (queryPurchaseMethod === "All") {
      refreshList();
    }
    else {
      find(queryLocation, "location")
    }
  };

  const findByCoupons = () => {

    find(queryCoupons, "couponUsed")
  };

  const findByPurchaseMethod = () => {
    if (queryPurchaseMethod === "Any") {
      refreshList();
    } else {
      find(queryPurchaseMethod, "purchaseMethod")
    }
  };

  const findAllFilters = () => {
    if(queryLocation === "Any") queryLocation = null
    if(queryPurchaseMethod === "All") queryPurchaseMethod = null
    SalesDataService.findAll(queryLocation, queryPurchaseMethod, queryCoupons)
    .then(response => {
      setSales(response.data.Sales)
      modifySalesFormat(sales)
    })
    .catch(e => {
        console.log(e)
    })
  }

  return (

    <div>
      <div className='row'>
        <div className="col-4">
        <div class="sticky-top ">
        <div class="nav flex-column">      
          <div className="input-group mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text" id="basic-addon2">Purchase Method</span>
              </div>
              <select class="form-control" type="custom-select" onChange={onChangeQueryPurchaseMethod}>
                {
                  purchaseMethods.map(method => {
                    return (
                      <option value={method}> {method} </option>
                    )
                  })
                }
              </select>
              <div className="input-group-append">
                <button className="btn btn-outline-dark" onClick={findByPurchaseMethod}>Apply</button>
              </div>
          </div>
        </div>
        <div className="input-group mb-3">
          <div class="input-group-prepend">
            <span class="input-group-text" id="basic-addon3">Location</span>
          </div>
          <select class="form-control" onChange={onChangeQueryLocation}>
              {
                locations.map(method => {
                  return (
                    <option value={method}> {method} </option>
                  )
                })
              }
            </select>
            <div className="input-group-append">
                <button className="btn btn-outline-dark" onClick={findByLocation}>Apply</button>
            </div>
        </div>
        <div className="input-group mb-3">        
        <div class="custom-control custom-switch">
          <input type="checkbox" class="custom-control-input" onChange={onChangeQueryCoupons} id="customSwitch1"/>
          <label class="custom-control-label" for="customSwitch1">Coupons</label>
        </div>
        </div>
        <div className="input-group mb-3">
            <label for="startDate">From</label>
            <DatePicker
								id="startDate"
								onChange={onChangeSetstartDate}
								selected={startDate}
							/>
            </div>
        <div className="input-group mb-3">
            <label for="startDate">To</label>
            <DatePicker
								id="startDate"
								onChange={onChangeSetendDate}
								selected={endDate}
							/>
        </div>  
        </div>  
        <div className="input-group-append">
          <button
            className="btn btn-outline-dark gray-200"
            type="button"
            onClick={findAllFilters}
          >
            Apply All Filters
          </button>
        </div>
        <br/>
        <br/>
        <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">STATS</h5>
                  <p className="card-text">
                    <strong>Total Sales : {totalSales}</strong>
                  </p>
                </div>
              </div>
            </div>
      </div>
      <div className="col">
      { 
        NoSalesFlag?
        <Chart
            chartType="Table"
            width="100%"
            height="100%"
            data={modifiedSales}
            options={{
              allowHtml: true,
              title: "Sales",
              legend: { position: "top" },
              pageSize: 50,
              width:"100%",
              height:"100%",
              backgroundColor: '#E4E4E4',
              is3D: true
            }}
        /> :
        <div class="alert alert-danger" role="alert">
          No such sales exist :(
        </div>
      } 
      </div>
      </div>
    </div>

  );
}

export default SalesList;
