import http from "../http-common";

class SalesDataService {
  getAll(page = 0) {
    return http.get(`/sales?page=${page}`);
  }

  find(query, by = "location", page = 0) {
    return http.get(`/sales?${by}=${query}&page=${page}`);
  } 

  findAll(location="All", purchaseMethod="Any", couponUsed="", page=0) {

    var queryLocation = (location=="All"?null:location)
    var queryPurchaseMethod = (purchaseMethod=="Any"?null:purchaseMethod)
    var queryCoupons = (couponUsed==""?null:couponUsed)

    var query = `/sales?page=${page}`
    if(queryLocation) {
      query+=`&location=${queryLocation}`
    }
    if(queryPurchaseMethod) {
      query+=`&purchaseMethod=${queryPurchaseMethod}`
    }
    if(queryCoupons) {
      query+=`&couponUsed=${couponUsed}`
    }

    console.log(query)

    return http.get(query) ;

  }

  get(id) {
    return http.get(`/sale/${id}`);
  }

  createSale(data) {
    return http.post("/sale", data);
  }

  deleteSale(id) {
    return http.delete(`/sale/${id}`);
  }

  getPurchaseMethod() { 
    return http.get('/purchaseMethods') ;
  }

  getStoreLocations() {
    return http.get('/storeLocations') ;
  }

  getUnlimited(query, by, page=0) {
    if(!query) {
      return http.get()
    }
    return http.get(`/sales?${by}=${query}&page=${page}&SalesPerPage=1000`)
  } 

}

export default new SalesDataService();
