import React, { useEffect } from 'react';
import SalesDataService from '../../services/sales.js' ;
import dayjs from 'dayjs' ;
import { Chart } from "react-google-charts";
import {Link,Routes,Route} from 'react-router-dom';

const Graph4 = props => {
    return (
      <div className='wrapper App container'>
          <div className="input-group mb-3">
              <div class="input-group-prepend">
                  <span class="input-group-text" id="basic-addon3">Year</span>
              </div>

              <input class="form-control" type="number" min="2013" max="2017" onChange={OnChangeSetYear} >
              </input>

              <div className="input-group-append">
                  <button
                  className="btn btn-outline-dark"
                  type="button"
                  onClick={DisplayPieForRevenueChart}
                  >
                  Analyze
                  </button>
              </div>
          </div>
        <div>
            {
              props.dataForBarChart!=null?( <div>
                <div class="jumbotron jumbotron-fluid">
                <div class="container">
                <h1 class="display-6">4. Comparison of product demand in stores</h1>
                </div></div>                
                <Chart
                  chartType="BarChart"
                  width="100%"
                  height="400px"
                  data={dataForBarChart}
                  options={{
                    title: `Demand for ${queryItem}`,
                    curveType: "function",
                    legend: { position: "bottom" } ,
                    hAxis: {
                      title: `Total Sales of ${queryItem}`,
                      minValue: 0,
                    },
                    vAxis: {
                      title: "City",
                    },
                  }}
                /> 
                </div>) :
                <div class="jumbotron jumbotron-fluid">
                  <div class="container">
                    <h1 class="display-6">4. Comparison of product demand in stores</h1>
                    <p class="lead text-italics">Enter an Item</p>
                  </div>
                </div>
            }
        </div>
      </div>

    )
}

export default Graph4
