import React, { useEffect } from 'react';
import SalesDataService from '../services/sales.js' ;
import dayjs from 'dayjs' ;

import { Chart } from "react-google-charts";

const AnalyzeSales = (props) => {
    // G1 : revenue from each all stores -> min & max = one graph has all stores 

    // G2 : percentage of different items sold from a given store - DONE

    const [store, setStore] = React.useState("")
    const [locations, setLocations] = React.useState([])
    const [dataForPiePlot, setdataForPiePlot] = React.useState([])
    const [dataForLineChart, setdataForLineChart] = React.useState([])
    const [sales, setSales] = React.useState([])
    const [month, setMonth] = React.useState("")
    const months = ['Jan', 'Feb','March','April','May','June','July','August','September','October','November','December'] 

  useEffect(() => {
    retrieveLocations();
  }, []);

    const retrieveLocations = () => {
        SalesDataService.getStoreLocations()
        .then(response => {
        // console.log(response.data);
        setLocations(response.data);
        })
        .catch(e => {
        console.log(e);
        });
    }

    const OnChangeSetStore = e => {
        setStore(e.target.value)
    }

    const OnChangeSetMonth = e => {
      console.log(e.target.value)
      for(var i=0;i<12;i++){
        if(months[i] === e.target.value) {
          setMonth(i) 
          return  
        }
      }
      return 
    }

    const calculateQuantityItems = () => {

        var items = {}
        // {'pencil':3, 'eraser':10}
        var totalNumber = 0
        // 13

        sales.map(element => {

            element.items.map(item => {

              items[item.name]=(items[item.name] ? items[item.name]+parseInt(item.quantity) : parseInt(item.quantity)) 

              totalNumber+=parseInt(item.quantity)
            })

        });

        var ratios = [['Category','Quantity sold']]

        var objectKeysArray = Object.keys(items)

        objectKeysArray.forEach((objKey) => {
            var objValue = items[objKey]
            // objVal holds quantity of each item
            var ration = [objKey, objValue]

            ratios.push(ration)
        })

        return ratios
    }

    const DisplayPieChart = () => {
        SalesDataService.find(store, "location")
                .then(response => {

                    setSales(response.data.Sales)
                })
                .catch(e => {
                    console.log(e);
                })

        const series = calculateQuantityItems()

        setdataForPiePlot(series)

    }

    const calculateMonthlyRevenue = (items) => {
      // console.log(items) ;
      var totalRevenue = 0.0
      items.map(item => {
        totalRevenue+=(parseInt(item.quantity)*item.price['$numberDecimal']) 
        // console.log(totalRevenue)
      })
      return totalRevenue
    }

    const DisplayLineChart = () => {

      // choose a month -> display line chart from that month      
      SalesDataService.getUnlimited(store, "location")
      .then(response => {        
          setSales(response.data.Sales)
      })
      .catch(e => {
          console.log(e);
      })

      var m = [["Date","Revenue"]]

      sales.map((sale)=>{
        const date = dayjs(sale.saleDate).month()
        if(date === month) {
          const d = dayjs(sale.saleDate).date()
          const r = calculateMonthlyRevenue(sale.items) 

          var flag = 0
          for(var i=0;i<m.length;i++) {
            if(m[i][0] === d) {
              m[i][1]+=r
              flag = 1
              break
            }
          }
          if(!flag) {
            m.push([d,r])
          }
        }
      })
      m.sort((a,b)=>a[0]-b[0])
      console.log(m)
      setdataForLineChart(m)

    }

    return (
      <div className="App">
          <div className="row form">            
              <div className="col">
                <div className="input-group mb-3">
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="basic-addon3">@</span>
                    </div>

                    <select class="form-control" onChange={OnChangeSetStore}>
                        {
                        locations.map(method => {
                            return (
                            <option value={method}> {method} </option>
                            )
                        })
                        }
                    </select>

                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={DisplayPieChart}
                        >
                        Analyze
                        </button>
                  </div>
              </div>
              </div>

              <div className="col">
                <div className="input-group mb-3">
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="basic-addon3">Month</span>
                    </div>

                    <select class="form-control" onChange={OnChangeSetMonth}>
                        {
                        months.map(method => {
                            return (
                            <option value={method}> {method} </option>
                            )
                        })
                        }
                    </select>

                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={DisplayLineChart}
                        >
                        Analyze
                        </button>
                    </div>
                </div>
              </div>
          </div>

          <div className="row">
            {
              {dataForPiePlot}.length>0?(<Chart
                width={'800px'}
                height={'600px'}
                chartType="PieChart"
                loader={<div>Loading Chart</div>}
                data={dataForPiePlot}
                options={{
                  title: 'Items and Sales',
                }}
              />) :
              'Select a Store to display chart'
            }

          </div>

          <div className="row">
            <Chart
              chartType="LineChart"
              width="100%"
              height="400px"
              data={dataForLineChart}
              options={{
                title: `Revenue in ${store} in the month of ${months[month]}`,
                curveType: "function",
                legend: { position: "bottom" },
              }}
            />    
          </div>

      </div> 
    );

}

export default AnalyzeSales;
