import React, { useEffect } from 'react';
import SalesDataService from '../../services/sales.js' ;
import dayjs from 'dayjs' ;
import { Chart } from "react-google-charts";
import {Link,Routes,Route} from 'react-router-dom';

const Graph3 = props => {
    return (
        <div className='wrapper App container'>

<div className="input-group mb-3">
                      <div class="input-group-prepend">
                          <span class="input-group-text" id="basic-addon3">Month</span>
                      </div>

                      <select class="form-control" onChange={OnChangeSetMonth}>
                          {
                          months.map(method => {
                              return (
                              <option value={method}> {method} </option>
                              )
                          })
                          }
                      </select>

                      <div className="input-group-append">
                          <button
                          className="btn btn-outline-dark"
                          type="button"
                          onClick={DisplayLineChart}
                          >
                          Analyze
                          </button>
                      </div>
                  </div>

            <div>
            {props.dataForLineChart!=null ?(
            <div>
            <div class="jumbotron jumbotron-fluid">
            <div class="container">
                <h1 class="display-6">3. Monthly Revenue Chart for A store</h1> </div></div>  
                <Chart
                chartType="LineChart"
                width="100%"
                height="400px"
                data={dataForLineChart}
                options={{
                    title: `Revenue in ${store} in the month of ${months[month]}`,
                    curveType: "function",
                    legend: { position: "bottom" },
                }}
                /> 
            </div>
        ):
        <div class="jumbotron jumbotron-fluid">
          <div class="container">
            <h1 class="display-6">3. Monthly Revenue Chart for A store</h1>
            <p class="lead text-italics">Select a Store and a Month</p>
          </div>
        </div>
}
            </div>
        </div>

    )
}

export default Graph3
