import React from 'react';
import SalesDataService from '../services/sales.js'
import { useEffect } from 'react';
import dayjs from 'dayjs' ;


const SalesList = props => {
  const [sales, setSales] = React.useState([])
  const [queryLocation, setQueryLocation] = React.useState("")
  const [queryCoupons, setQueryCoupons] = React.useState(false)
  const [queryPurchaseMethod, setQueryPurchaseMethod] = React.useState("")
  const [purchaseMethods, setPurchaseMethods] = React.useState(["Any"]);
  const [pages, setQueryPage] = React.useState(0) 
  const [locations,  setLocations] = React.useState(["All"])
  const [showItems, onChangeQueryItems] = React.useState()

  useEffect(() => {
    retrieveSales();
    retrievePurchaseMethod();
    retrieveLocations();
    setQueryCoupons(false);
  }, []);

  const onChangeQueryLocation = e => {
    const searchLocation = e.target.value;
    setQueryLocation(searchLocation);
  };

  const onChangeQueryCoupons = e => {
    const searchCoupons = (e.target.checked?false:true)
    setQueryCoupons(searchCoupons);
    findByCoupons();
  };

  const onChangeQueryPurchaseMethod = e => {
    const searchPurchaseMethod = e.target.value;
    setQueryPurchaseMethod(searchPurchaseMethod);
  };

  const onChangePages = e => {
    const page = e.target.value 
    setQueryPage(page) 
  }

  const retrieveSales = () => {
    console.log('here')
    SalesDataService.getAll()
      .then(response => {
        console.log(response.data);
        setSales(response.data.Sales);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const retrievePurchaseMethod = () => {
    SalesDataService.getPurchaseMethod()
      .then(response => {
        console.log(response.data);
        setPurchaseMethods(["Any"].concat(response.data));
      })
      .catch(e => {
        console.log(e);
      });
  };

  const retrieveLocations = () => {
    SalesDataService.getStoreLocations()
    .then(response => {
      // console.log(response.data);
      setLocations(["All"].concat(response.data));
    })
    .catch(e => {
      console.log(e);
    });
  }

  const refreshList = () => {
    retrieveSales();
  };

  const find = (query, by) => {
    SalesDataService.find(query, by)
      .then(response => {
        console.log(response.data);
        setSales(response.data.Sales);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const findAll = () => {
    SalesDataService.getAll(pages)
      .then(response => {
          setSales(response.data.Sales)
      })
  }

  const findByLocation = () => {
    if (queryPurchaseMethod === "All") {
      refreshList();
    }
    else {
      find(queryLocation, "location")
    }
  };

  const findByCoupons = () => {
    find(queryCoupons, "couponUsed")
  };

  const findByPurchaseMethod = () => {
    if (queryPurchaseMethod === "Any") {
      refreshList();
    } else {
      find(queryPurchaseMethod, "purchaseMethod")
    }
  };


  return (
    <div>

      <div className="row form">

        <div className="col">
        <div className="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text" id="basic-addon1">Pg No</span>
        </div>
          <input
            type="text"
            className="form-control"
            placeholder="Search by coupons"
            value={pages}
            onChange={onChangePages}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findAll}
            >
              Search
            </button>
          </div>
        </div> </div>
        <div className="col">
        <div className="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text" id="basic-addon2">Purchase Method</span>
        </div>
          <select class="form-control" type="custom-select" onChange={onChangeQueryPurchaseMethod}>
            {
              purchaseMethods.map(method => {
                return (
                  <option value={method}> {method} </option>
                )
              })
            }
          </select>
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByPurchaseMethod}
            >
              Search
            </button>
          </div>

        </div></div>
        <div className="col">
        <div className="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text" id="basic-addon3">@</span>
        </div>
        <select class="form-control" onChange={onChangeQueryLocation}>
            {
              locations.map(method => {
                return (
                  <option value={method}> {method} </option>
                )
              })
            }
          </select>
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByLocation}
            >
              Search
            </button>
          </div>
        </div></div><br/><br/>
        <div class="input-group mb-3">
        <label for="mySwitch">
          Using Coupons
        </label>
          <div class="form-check form-switch">
            <input class="form-check-input" type="checkbox" id="mySwitch" onChange={onChangeQueryCoupons}/>
          </div>
        </div><br/><br/>
        <div class="input-group mb-3">
        <label for="mySwitch2">
          Show Items
        </label>
          <div class="form-check form-switch">
            <input class="form-check-input" type="checkbox" id="mySwitch2" onChange={onChangeQueryItems}/>
          </div>
        </div><br/><br/>

      </div>

      <div className="row">
        {

        sales.map((sale) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{sale.storeLocation}</h5>
                  <p className="card-text">
                    <strong>Customer :  </strong>{sale.customer.email}<br/>
                    <strong>Ratings : </strong>{sale.customer.satisfaction}<br/>
                    <strong>Purchase Method : </strong>{sale.purchaseMethod}<br/>
                    <strong>Coupon Used :</strong>{
                      sale.couponUsed?" YES":" NO"
                    }<br/>
                    <strong>Date :</strong>{" " +dayjs(sale.saleDate).format('DD/MM/YYYY')}<br/>
                  </p>
                  {/* <div className="row">
                  <Link to={"/sales/"+sale._id} className="btn btn-primary col-lg-5 mx-1 mb-1">
                    View Reviews
                  </Link>
                  <a target="_blank" href={"https://www.google.com/maps/place/" + address} className="btn btn-primary col-lg-5 mx-1 mb-1">View Map</a>
                  </div> */}
                </div>
              </div>
            </div>
          );
        })

        }


      </div>

    </div>
  );
}

export default SalesList;
