import React, { useEffect } from 'react';
import SalesDataService from '../services/sales.js' ;

import { Chart } from "react-google-charts";

const AnalyzeSales = (props) => {
    // G1 : revenue from each all stores -> min & max = one graph has all stores

    // G2 : percentage of different items sold from a given store

    const [store, setStore] = React.useState("")
    const [locations, setLocations] = React.useState([])
    const [dataToPlot, setdataToPlot] = React.useState([])
    const [sales, setSales] = React.useState([])

  useEffect(() => {
    retrieveLocations();
  }, []);

    const retrieveLocations = () => {
        SalesDataService.getStoreLocations()
        .then(response => {
        // console.log(response.data);
        setLocations(response.data);
        })
        .catch(e => {
        console.log(e);
        });
    }

    const OnChangeSetStore = e => {
        setStore(e.target.value)
    }

    const labelContent = (props) => {
        let formatedNumber = Number(props.dataItem.value)        
        return `${props.dataItem.category} item: ${formatedNumber}`;
      };

      const ChartContainer = () => (
        <Chart>
          <ChartTitle text="Ratios of Items Sold" />
          <ChartLegend position="bottom" />
          <ChartSeries>
            <ChartSeriesItem
              type="pie"
              data={dataToPlot}
              field="value"
              categoryField="category"
              labels={{ visible: true, content: labelContent }}
            />
          </ChartSeries>
        </Chart>
      );

      const calculateRatioItems = () => {

        var items = {}
        // {'pencil':3, 'eraser':10}
        var totalNumber = 0
        // 13

        sales.map(element => {

            element.items.map(item => {

              items[item.name]=(items[item.name] ? items[item.name]+parseInt(item.quantity) : parseInt(item.quantity)) 

              totalNumber+=parseInt(item.quantity)
            })

        });


        var ratios = []

        var objectKeysArray = Object.keys(items)

        objectKeysArray.forEach((objKey) => {
            var objValue = items[objKey]
            // objVal holds quantity of each item
            var ratio = objValue/totalNumber
            var newJsonObject = {
                'category': objKey,
                'value': ratio
            }
            ratios.push(newJsonObject)
        })

        return ratios
    }

      const DisplayPieChart = (e) => {
        SalesDataService.find(store, "location")
                .then(response => {

                    setSales(response.data.Sales)
                })
                .catch(e => {
                    console.log(e);
                })

        const series = calculateRatioItems()

        setdataToPlot(series)

    }

      return (
        <div>
            <div className="row form">            
                <div className="input-group mb-3">
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="basic-addon3">@</span>
                    </div>

                    <select class="form-control" onChange={OnChangeSetStore}>
                        {
                        locations.map(method => {
                            return (
                            <option value={method}> {method} </option>
                            )
                        })
                        }
                    </select>

                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={DisplayPieChart}
                        >
                        Analyze
                        </button>
                    </div>
                </div>
            </div>


             <Chart>
                <ChartTitle text="Percentage of Items Sold" />
                <ChartLegend position="bottom" />
                    <ChartSeries>
                        <ChartSeriesItem
                        type="pie"
                        data={dataToPlot}
                        field="value"
                        categoryField="category"
                        labels={{ visible: true, content: labelContent }}
                        />
                    </ChartSeries>
              </Chart>




         </div> 

      );

}

export default AnalyzeSales;
