import React, { useEffect, useState } from 'react';
import {Link,Routes,Route} from 'react-router-dom';
import StoresDataService from '../services/stores.js' ;
import SalesDataService from '../services/sales.js' ;

const ManagementTools = (props) => {
    const [stores, setStores] = useState([])    
    const [numStores, setNumStores] = useState(0)
    const [currStore, setCurrStore] = useState(0)
    const [revenue, setRevenue] = useState([]) // each index for each store

  useEffect(() => {
    retrieveLocations()
    retrieveSales()
  }, []);

  const retrieveSales = () => {
    var listRevenue = []
    stores.each(loc=>{
      SalesDataService.find(loc,"location")
        .then(res=>{
          const sales = res.data.Sales
          var revenuel=0.0
          sales.each(sale=>{
            sale.items.each(item=>{
              revenuel+=(parseInt(item.quantity)*item.price['$numberDecimal'])
            })
          })
          listRevenue.push({"loc":loc,"rev":revenuel})
        })
        .catch(e=>{console.log(e.message);})
    })
    console.log(listRevenue)
    // setRevenue(listRevenue)
    // console.log(listRevenue)
    console.log(stores)
  }

  const calculateRevenue = () => {

  }

  const retrieveLocations = () => {
    StoresDataService.getStores()
      .then(res=>{
        const data = res.data.Stores
        var names = []
        data.map(store=>{
          names.push(store.location)
        })
        setStores(names)
        setNumStores(res.data.Stores.length)        
      })
      .catch(e=>{console.log(e.message)})
  }

  const nextStore = e => {
    const next = (currStore+1)%numStores
    setCurrStore(next)
  }

  const prevStore = e => {
    alert('prev')
    const prev = (currStore>0?(currStore-1):(numStores-1))
    setCurrStore(prev)
  }

    return (
      <div>
      <div className='row'></div>
      <div className='row py-5'> {
        props.loggedIn ? (
        <div class="container">
          <div className='row'>
            <div className='col'>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-caret-left-fill" viewBox="0 0 16 16" onClick={prevStore}>
                <path d="m3.86 8.753 5.482 4.796c.646.566 1.658.106 1.658-.753V3.204a1 1 0 0 0-1.659-.753l-5.48 4.796a1 1 0 0 0 0 1.506z"/>
              </svg>
            </div>
            <div className='col-8'>
              <div className='text-centre'><h1>{stores[currStore]}</h1></div>

            </div>
            <div className='col'><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-caret-right-fill" viewBox="0 0 16 16" onClick={nextStore}>
                <path d="m12.14 8.753-5.482 4.796c-.646.566-1.658.106-1.658-.753V3.204a1 1 0 0 1 1.659-.753l5.48 4.796a1 1 0 0 1 0 1.506z"/>
              </svg>
            </div>
         </div>
        </div>
       ) :
       (
       <div class="jumbotron jumbotron-fluid">
         <div class="container">
           <h1 class="display-4">Log In to continue</h1>
           <p class="lead">Head on to <Link to='/supply'>Login Page</Link></p>
         </div>
       </div>
       )
       }
      </div>
      </div>
    );
}

export default ManagementTools;
