import React, { useEffect } from 'react';
import SalesDataService from '../services/sales.js' ;
import dayjs from 'dayjs' ;
import { Chart } from "react-google-charts";

const PredictSales = (props) => {
    // G1 : revenue from each all stores -> min & max = one graph has all stores 

    // G2 : percentage of different items sold from a given store - DONE

    const [store, setStore] = React.useState("")
    const [locations, setLocations] = React.useState([])
    const [dataForPiePlot, setdataForPiePlot] = React.useState(null)
    const [dataForLineChart, setdataForLineChart] = React.useState(null)
    const [dataForBarChart, setdataForBarChart] = React.useState(null)
    const [sales, setSales] = React.useState([])
    const [month, setMonth] = React.useState("")
    const months = ['Jan', 'Feb','March','April','May','June','July','August','September','October','November','December'] 
    const [queryItem, setItem] = React.useState('')
    const [queryYear, setQueryYear] = React.useState()
    const [dataForAnnualPie, setDataForAnnualPie] = React.useState(null)


    return (
      <div class="container">
        <div class="row py-3">
          <div class="col-4" id="sticky-sidebar">
            <div class="sticky-top ">
                <div class="nav flex-column">      
                  <div className="input-group mb-3">
                      <div class="input-group-prepend">
                          <span class="input-group-text" id="basic-addon3">Store Location</span>
                      </div>

                      <select class="form-control" onChange={OnChangeSetStore}>
                          {
                          locations.map(method => {
                              return (
                              <option value={method}> {method} </option>
                              )
                          })
                          }
                      </select>
                  </div>
                  <div className="input-group mb-3">
                      <div class="input-group-prepend">
                          <span class="input-group-text" id="basic-addon3">Month</span>
                      </div>

                      <select class="form-control" onChange={OnChangeSetMonth}>
                          {
                          months.map(method => {
                              return (
                              <option value={method}> {method} </option>
                              )
                          })
                          }
                      </select>

                      <div className="input-group-append">
                          <button
                          className="btn btn-outline-secondary"
                          type="button"
                          onClick={DisplayLineChart}
                          >
                          Analyze
                          </button>
                      </div>
                  </div>
                </div>
            </div>
          </div>
          <div class="col">
            <table class="columns">

            </table>
          </div>
        </div> 
      </div>
    );

}

export default PredictSales;
