import './App.css';
import React from 'react';
import { Route, Routes, Link  } from 'react-router-dom' ;
import "bootstrap/dist/css/bootstrap.min.css" ;
import  SalesList  from './components/sales-list.js'
import  AddSale  from './components/add-sale.js'
import  DeleteSale  from './components/delete-sale.js'
import  Login  from './components/login.js'
import  Sale  from './components/sale.js'
import Home from './components/home.js'
import AnalyzeSales from './components/analyze.js'

function App() {

  const [user, setUser] = React.useState(null)

  async function login(user) {
    setUser(user)
  }

  async function logout() {
      setUser(null)
  }

  return (
      <div>

         <nav className="navbar navbar-expand navbar-dark bg-dark">
              <a href="/supply" className="navbar-brand">
                Einduch Stop
              </a>

              <div className="navbar-nav mr-auto">

              { 
                  user ? (
                  <li className="nav-item" >
                    <a onClick={logout} className="nav-link" style={{cursor:'pointer'}}>
                      Logout {user.name}
                    </a>
                  </li>
                    ) : (           
                  <Link to="/login">
                      <li className="nav-item" >
                        <a className="nav-link">Login</a>
                      </li>
                  </Link>
                  )
            }


                  <Link to="/supply/sales">
                      <li class="nav-item">
                        <a className="nav-link" >Monitor Sales</a>
                      </li>
                  </Link>
                  <Link to="/supply/sales">
                      <li class="nav-item">
                        <a className="nav-link" >Monitor Sales</a>
                      </li>
                  </Link>


                    <li class="nav-item">
                      <a class="nav-link disabled" href="#">Disabled</a>
                    </li>

              </div>
          </nav> 

        <div className="container mt-3">
           <Routes>
              <Route path="/supply" element={<Home />}/>

              <Route path="/supply/sales" element={<SalesList />}/>

              <Route 
                  path="/supply/sale/:id"
                  render={(props) => (
                    <Sale {...props} user={user} />
                  )}
                />

                {/* <Route 
                  path="/supply/sale"
                  render={(props) => (
                    <AddSale {...props} user={user} />
                  )}
                /> */}

                <Route 
                  path="/login"
                  element={<Login/>}
                />

                <Route 
                  path='/analyze'
                  element={<AnalyzeSales/>}
                />

           </Routes> 
        </div> 

    </div>
  );
}

export default App;
