import React, { useEffect } from 'react';
import SalesDataService from '../services/sales.js' ;
import dayjs from 'dayjs' ;

import { Chart } from "react-google-charts";

const AnalyzeSales = (props) => {
    // G1 : revenue from each all stores -> min & max = one graph has all stores 

    // G2 : percentage of different items sold from a given store - DONE

    const [store, setStore] = React.useState("")
    const [locations, setLocations] = React.useState([])
    const [dataForPiePlot, setdataForPiePlot] = React.useState(null)
    const [dataForLineChart, setdataForLineChart] = React.useState(null)
    const [dataForBarChart, setdataForBarChart] = React.useState(null)
    const [sales, setSales] = React.useState([])
    const [month, setMonth] = React.useState("")
    const months = ['Jan', 'Feb','March','April','May','June','July','August','September','October','November','December'] 
    const [queryItem, setItem] = React.useState('')
    const [queryYear, setQueryYear] = React.useState()
    const [dataForAnnualPie, setDataForAnnualPie] = React.useState(null)

  useEffect(() => {
    retrieveLocations();
  }, []);

    const retrieveLocations = () => {
        SalesDataService.getStoreLocations()
        .then(response => {
        // console.log(response.data);
        setLocations(response.data);
        })
        .catch(e => {
        console.log(e);
        });
    }
    // triggered setters
    const OnChangeSetStore = e => {
        setStore(e.target.value)
    }

    const OnChangeSetMonth = e => {
      console.log(e.target.value)
      for(var i=0;i<12;i++){
        if(months[i] === e.target.value) {
          setMonth(i) 
          return  
        }
      }
      return 
    }

    const OnChangeSetItem = (e) => {
      setItem(e.target.value.toLowerCase())
    }

    const OnChangeSetYear = e => {
      setQueryYear(e.target.value)
    }

    // helper functions
    const calculateQuantityItems = () => {

        var items = {}
        // {'pencil':3, 'eraser':10}
        // 13
        sales.map(element => {

            element.items.map(item => {

              items[item.name]=(items[item.name] ? items[item.name]+parseInt(item.quantity) : parseInt(item.quantity)) 

            })
        });
        var ratios = [['Category','Quantity sold']]
        var objectKeysArray = Object.keys(items)
        objectKeysArray.forEach((objKey) => {
            var objValue = items[objKey]
            // objVal holds quantity of each item
            var ration = [objKey, objValue]
            ratios.push(ration)
        })

        return ratios
    }

    const calculateTotalRevenue = (items) => {
      // console.log(items) ;
      var totalRevenue = 0.0
      items.map(item => {
        totalRevenue+=(parseInt(item.quantity)*item.price['$numberDecimal']) 
      })
      return totalRevenue
    }

    // display graphs
    const DisplayPieChart = () => {
        SalesDataService.find(store, "location")
                .then(response => {       
                    setSales(response.data.Sales)
                })
                .catch(e => {
                    console.log(e);
                })

        const series = calculateQuantityItems()

        setdataForPiePlot(series)
    }

    const DisplayLineChart = () => {      
      // choose a month -> display line chart from that month      
      SalesDataService.getUnlimited(store, "location")
      .then(response => {        
          setSales(response.data.Sales)
      })
      .catch(e => {
          console.log(e);
      })

      var m = [["Date","Revenue"]]
      var monthlySaleExists = false 
      sales.map((sale)=>{
        const date = dayjs(sale.saleDate).month()
        if(date === month) {
          monthlySaleExists = true ;
          const d = dayjs(sale.saleDate).date()
          const monthlyRevenue = calculateTotalRevenue(sale.items) 

          var flag = 0
          for(var i=0;i<m.length;i++) {
            if(m[i][0] === d) {
              m[i][1]+=monthlyRevenue
              flag = 1
              break
            }
          }
          if(!flag) {
            m.push([d,monthlyRevenue])
          }
        }
      })
      if(!monthlySaleExists)
        setdataForLineChart(null)
      else {
        m.sort((a,b)=>a[0]-b[0])
        setdataForLineChart(m)
      }

    }

    const DisplayPieForRevenueChart = () => {
      // as pie chart comparing all stores
      SalesDataService.getUnlimited()
      .then(response => {       
          setSales(response.data.Sales)
      })
      .catch(e => {
          console.log(e);
      })

      var m = [["Store","Annual Revenue"]]

      sales.map((sale)=>{
        const year = dayjs(sale.saleDate).year()
        if(year == queryYear) {
          const annualRevenue = calculateTotalRevenue(sale.items) 

          var flag = 0
          for(var i=1;i<m.length;i++) {
            if(m[i][0] === sale.storeLocation) {
              m[i][1]+=annualRevenue
              flag = 1
              break
            }
          }
          if(!flag) {
            m.push([sale.storeLocation, annualRevenue])
          }
        }
      })

      setDataForAnnualPie(m)
    }

    const DisplayBarChart = () => {
      // comparison of sales of the given item in all outlets
      // ACTIONS : increase the cost of the item in that branch , increase supply of the item and relevant ones to tht branch

      SalesDataService.getAll()
      .then(response => {
        setSales(response.data.Sales);
      })
      .catch(e => {
        console.log(e);
      });

        var quant = {}        
        var itemSold=false
        sales.map(sale => {
          sale.items.map(item => {
            if(item.name === queryItem) {
              itemSold=true
              quant[sale.storeLocation] = (quant[sale.storeLocation] ? quant[sale.storeLocation] + parseInt(item.quantity) : parseInt(item.quantity))
            }
          })
        })
        if(!itemSold){
          setdataForBarChart(null)
          return
        }       
        var data = [['Store', 'Quantity']]

        var objectKeysArray = Object.keys(quant)

        objectKeysArray.forEach((objKey) => {
            var objValue = quant[objKey]
            console.log(objKey, objValue)
            // objVal holds  of each item
            var q = [objKey, objValue]
            data.push(q)            
        })

        setdataForBarChart(data) 
    }

    return (
      <div class="container">
        <div class="row py-3">
          <div class="col-3" id="sticky-sidebar">
            <div class="sticky-top">
            <div class="nav flex-column">          
                  <div className="col">
                    <div className="input-group mb-3">
                        <div class="input-group-prepend">
                            <span class="input-group-text" id="basic-addon3">@</span>
                        </div>

                        <select class="form-control" onChange={OnChangeSetStore}>
                            {
                            locations.map(method => {
                                return (
                                <option value={method}> {method} </option>
                                )
                            })
                            }
                        </select>

                        <div className="input-group-append">
                            <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={DisplayPieChart}
                            >
                            Analyze
                            </button>
                        </div>
                    </div>
                  </div>
                  <div className="col">
                    <div className="input-group mb-3">
                        <div class="input-group-prepend">
                            <span class="input-group-text" id="basic-addon3">Month</span>
                        </div>

                        <select class="form-control" onChange={OnChangeSetMonth}>
                            {
                            months.map(method => {
                                return (
                                <option value={method}> {method} </option>
                                )
                            })
                            }
                        </select>

                        <div className="input-group-append">
                            <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={DisplayLineChart}
                            >
                            Analyze
                            </button>
                        </div>
                    </div>
                  </div>
                  <div className="col">
                    <div className="input-group mb-3">
                        <div class="input-group-prepend">
                            <span class="input-group-text" id="basic-addon3">Item</span>
                        </div>

                        <input class="form-control"  onChange={OnChangeSetItem}>
                        </input>

                        <div className="input-group-append">
                            <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={DisplayBarChart}
                            >
                            Analyze
                            </button>
                        </div>
                    </div>
                  </div> 
                  <div className="col">
                    <div className="input-group mb-3">
                        <div class="input-group-prepend">
                            <span class="input-group-text" id="basic-addon3">Year</span>
                        </div>

                        <input class="form-control" type="number" min="2014" max="2017" onChange={OnChangeSetYear} >
                        </input>

                        <div className="input-group-append">
                            <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={DisplayPieForRevenueChart}
                            >
                            Analyze
                            </button>
                        </div>
                    </div>    
                  </div>  
              </div>
            </div>
          </div>

          <div className="row">
            {
              dataForPiePlot?(<Chart
                width={'800px'}
                height={'600px'}
                chartType="PieChart"
                loader={<div>Loading Chart</div>}
                data={dataForPiePlot}
                options={{
                  title: `Percentage of items Sold in ${store}`,
                }}
              />) :
              'Select a store to display sales percentage of items'
            }

          </div>
          <div className="row">
          {
              dataForAnnualPie?(<Chart
                width={'800px'}
                height={'600px'}
                chartType="PieChart"
                loader={<div>Loading Chart</div>}
                data={dataForAnnualPie}
                options={{
                  title: `% Revenue per outlet in the year ${queryYear}`,
                }}
              />) :
              'Select a year to compare Annual Sales of all stores'
            }
          </div>
          <div className="row">
            {
              dataForLineChart!=null?(<Chart
                chartType="LineChart"
                width="100%"
                height="400px"
                data={dataForLineChart}
                options={{
                  title: `Revenue in ${store} in the month of ${months[month]}`,
                  curveType: "function",
                  legend: { position: "bottom" },
                }}
              /> ) :
              'Select a Store and a Month to display Revenue chart for that store in that month'
            }

          </div>
          <div className="row">
            {
              dataForBarChart!=null?(<Chart
                chartType="BarChart"
                width="100%"
                height="400px"
                data={dataForBarChart}
                options={{
                  title: `Demand for ${queryItem}`,
                  curveType: "function",
                  legend: { position: "bottom" } ,
                  hAxis: {
                    title: `Total Sales of ${queryItem}`,
                    minValue: 0,
                  },
                  vAxis: {
                    title: "City",
                  },
                }}
              /> ) :
              'Enter an Item to compare the demand in all stores, else entered item is not sold in any store'
            }

          </div>
        </div>
      </div> 
    );

}

export default AnalyzeSales;
